sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }

    g_form.getReference('u_car', doProcessing);

    //g_form.addErrorMessage("Employee car color is: " + issueCar.color);
}

function doProcessing(caller) {
    //g_form.addErrorMessage("Employee car supports charging? " + caller.u_supports_charging);
    if (caller.u_supports_charging == 'true') {
        g_form.addDecoration('u_car', 'icon-power', 'This car is electric');
    }
}
